config.default:
server {
	listen 80 default_server;
	listen [::]:80 default_server;

	root {{ document_root }};

	index index.php index.html;

	server_name _;

	location / {
		try_files $uri $uri/ =404;
	}

	location ~ .php$ {
		include snippets/fastcgi-php.conf;
		fastcgi_pass {{ fpm_socket }};
	}
}

